import base64
print("=======start=================")
origin ="this is for base code"
print("origin:",origin)
encode =base64.b64encode(bytes(origin,'utf-8'))
print('encode',encode)
decode  = base64.b64decode(encode)
print('decode',decode)
print("success" if origin==str(decode,'utf-8') else "fail")


